import Navbar from './components/Navbar';
import SignUp from './components/SignUp';
import { BrowserRouter as Router,Switch,Route} from "react-router-dom";
import Login from './components/Login';
import React, {useState} from 'react';
import Home from './components/Home';



function App() {

  const [mode , setMode] = useState('light');

  const toggleMode = () =>{
    if(mode==='light'){
      setMode('dark');
      document.body.style.backgroundColor= '#090910';
      document.title="ResearchNet - Dark Mode";
    }
    else{
      setMode('light');
      document.body.style.backgroundColor= 'white';
      document.title="ResearchNet - Light Mode";
    }
  }


  return (
    <Router>

     <Home />

      <Switch>    
          <Route path="/sign">
           <SignUp mode={mode}/>
          </Route>

          <Route path="/login">
            <Login mode={mode}/>
          </Route>

          <Route path="/">
            <Home/>
          </Route>

      </Switch>
    </Router>
  );
}

export default App;
